	//Regiester control's property binding.
	private void registerBindings(){
		dataBinder.registerBinding("creator", com.kingdee.eas.base.permission.UserInfo.class, this.prmtCreator, "data");
		dataBinder.registerBinding("createTime", java.sql.Timestamp.class, this.pkCreateTime, "value");
		dataBinder.registerBinding("number", String.class, this.txtNumber, "text");
		dataBinder.registerBinding("auditor", com.kingdee.eas.base.permission.UserInfo.class, this.prmtAuditor, "data");
		dataBinder.registerBinding("name", String.class, this.txtName, "text");
		dataBinder.registerBinding("auditTime", java.util.Date.class, this.pkAuditTime, "value");
		dataBinder.registerBinding("curProject", com.kingdee.eas.fdc.basedata.CurProjectInfo.class, this.prmtCurProject, "data");
		dataBinder.registerBinding("entrys.costAccount", com.kingdee.eas.fdc.basedata.CostAccountInfo.class, this.kdtEntrys, "costAccountNumber.text");
		dataBinder.registerBinding("entrys.workload", java.math.BigDecimal.class, this.kdtEntrys, "workload.text");
		dataBinder.registerBinding("entrys.unit", com.kingdee.eas.basedata.assistant.MeasureUnitInfo.class, this.kdtEntrys, "unit.text");
		dataBinder.registerBinding("entrys.price", java.math.BigDecimal.class, this.kdtEntrys, "price.text");
		dataBinder.registerBinding("entrys.adjustAmt", java.math.BigDecimal.class, this.kdtEntrys, "adjustAmount.text");
		dataBinder.registerBinding("entrys.product", com.kingdee.eas.fdc.basedata.ProductTypeInfo.class, this.kdtEntrys, "product.text");
		dataBinder.registerBinding("entrys", com.kingdee.eas.fdc.aimcost.AimAimCostAdjustEntryInfo.class, this.kdtEntrys, "userObject");
		dataBinder.registerBinding("entrys.costAccount.name", String.class, this.kdtEntrys, "costAccountName.text");
		dataBinder.registerBinding("entrys.description", String.class, this.kdtEntrys, "description.text");
		dataBinder.registerBinding("entrys.adjustType", com.kingdee.eas.fdc.basedata.AdjustTypeInfo.class, this.kdtEntrys, "adjustType.text");
		dataBinder.registerBinding("entrys.changeReason", String.class, this.kdtEntrys, "changeReason.text");		
	}
